function loadFile4Path(t) {
    var timeRandom = Math.floor(Date.now() / 1000);
    var p = "/" + t.Key + "/" + t.Name + ".html";
    var ND = t.Name.charAt(0).toUpperCase() + t.Name.slice(1);
    document.write('<script src="' + p.replace(".html", "Controller.js?v=" + timeRandom) + '"></script>');
    document.write('<script>app.directive("bakco' + ND + '", function () {return {restrict: "CAE",controller: "' + ND.toLowerCase() + 'Controller",templateUrl: "' + p + '"}});</script>');
}
var app = angular.module("rootApp", ["ngRoute"]);

app.directive('onErrorSrc', function() {
    return {
        link: function(scope, element, attrs) {
            element.bind('error', function() {
                if (attrs.src != attrs.onErrorSrc) {
                    attrs.$set('src', attrs.onErrorSrc);
                }
            });
        }
    }
});
for (x in sysPath) {
    loadFile4Path(sysPath[x]);
}
app.controller("phantrangController", phantrangController);
function phantrangController($scope) {

    $scope.getArray = function(curentIndex, totalPages) {
        curentIndex = parseInt(curentIndex);
        var a = new Array();
        var start = curentIndex - 3;
        start = Math.max(start, 1);
        var end = curentIndex + 3;
        end = Math.min(end, totalPages);
        for (var i = start; i <= end; i++) {
            a.push(i);
        }
        return a;
    }
    $scope.getArrayInt = function(totalPages) {
        var a = new Array();
        for (var i = 1; i <= totalPages; i++) {
            a.push(i);
        }
        return a;
    }
    $scope.LinkPhanTrang = function(pagesIndex, pagesNumber) {
        if ($scope.pagesLink == null) {
            return;
        }
        var str = $scope.pagesLink;
        var res = str.replace("[i]", pagesIndex);
        res = res.replace("[j]", pagesNumber);
        return res;
    }

}
app.directive("phanTrang", function() {
    return {
        restrict: 'AEC',
        templateUrl: "/public/ang1/PhanTrang/phantrang.html",
        controller: "phantrangController",
        scope: {
            pagesIndex: '=',
            pagesNumber: '=',
            pagesTotal: '=',
            pagesParams: '=',
            pagesLink: '@',
            ctrlFn: '&onClick',
        },
        replace: true,
        transclude: false,
        link: function(scope, element, attrs, controller) {
            scope.onClick = function(seachProduct, pagesIndex, pagesNumber) {
                console.log(scope.pagesParams);
                if (typeof (scope.ctrlFn) == 'function') {
                    scope.ctrlFn({"Params": scope.pagesParams, "pagesIndex": pagesIndex, "pagesNumber": pagesNumber});
                }
            }
        }
    };
});